import styled from 'styled-components';

export const MainPart = styled.section`
 background: #fff;
 width: 100%;
 height: 100%;
 display: flex;
 flex-direction: row;
 align-items: center;
`

export const MainText = styled.div`
 display: flex;
 flex-direction: column;
 align-items: center;
 justify-content: center;
 width: 150vh;
 height: 70vh;
 font-size: 1.2rem;
 background: #131313;
 color: #fff;
`

export const MainImage = styled.img`
  -webkit-filter: grayscale(100%); 
  filter: grayscale(100%);
  width: 300px;
  height: auto;
  z-index: 1000;
  justify-self: center;
`
