import styled from 'styled-components';

export const InfoContainer = styled.section`
 display: flex;
 flex-direction: column;
 align-items: center;
 justify-items: center;
 height: 100vh;
`

export const InfoText = styled.div`
 display: flex;
 flex-direction: column;
 align-items: center;
 justify-items: center;
`
export const InfoTitle = styled.h1`
 color: #f08080;
 font-size: 2.5rem;
 margin-bottom: 1rem;
`

export const Infos = styled.p`
 line-height: 2.5rem;
 text-align: center;
 font-size: 1.5rem;
 color: #131313;
 margin: 0 4rem;
`

export const ContactsWrapper = styled.div`
 display: grid;
 grid-template-columns: 1fr 1fr 1fr 1fr;
 grid-gap: 1rem;
 align-items: center;
 justify-content: flex-start;
`

export const Icon = styled.a`
 color: #131313;
 font-size: 3rem;
 margin: 2rem 1rem;
 transition: .3s ease-in;
 text-decoration: none;
 cursor: pointer;

 &:hover {
     color: #f08080;
     transition: .3s ease-in;
 }
`
export const CvButton = styled.button`
 outline: none;
 border: none;
 border-radius: 10px;
 background: #f08080;
 margin: 1rem;
 padding: 1.5rem 2rem;

}
`

export const CvLink = styled.a`
 font-size: 1.5rem;
 text-decoration: none;
 color: #131313;
 cursor: pointer;


 &:hover {
    color: #fff;
`
