import styled from 'styled-components';

export const MainPart = styled.section`
 background: #fff;
 width: 100%;
 height: 100%;
 display: flex;
 flex-direction: row;
 align-items: center;
 position: absolute;
 top: 0;
 left: 0;
`

export const MainText = styled.div`
 display: flex;
 flex-direction: column;
 align-items: flex-start;
 justify-content: center;
 width: 70vw;
 height: 70vh;
 background: #131313;
 padding: 0 5rem;
`
export const Intro = styled.h1`
 color: #fff;
 font-size: 2.5rem;
`
export const P = styled.p`
 color: #fff;
 font-size: 2rem;
 margin: 1rem 0;
`

export const Name = styled.span`
 margin: 0 1rem;
 font-size: 2.5rem;
 color: #f08080;
`

export const MainImage = styled.img`
  -webkit-filter: grayscale(100%); 
  filter: grayscale(100%);
  width: 250px;
  height: auto;
  z-index: 1000;
  position: absolute;
  top: 100px;
  left: 830px;
`
