import styled from 'styled-components';

export const ProjectsContainer = styled.div`
 display: flex;
 flex-direction: column;
 align-items: center;
 justify-items: center;
`

export const Title = styled.h1`
 color: #f08080;
 font-size: 2.5rem;
 margin-bottom: 1rem;

 @media screen and (max-width: 500px) {
     font-size: 1.5rem;
 }
`
